import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  Image,
  ScrollView,
} from "react-native";
import Constants from "expo-constants";
import { FontAwesome, Ionicons, AntDesign } from "@expo/vector-icons";
import { useNavigationState } from "@react-navigation/core";
import AsyncStorage from "@react-native-async-storage/async-storage";

const height = Dimensions.get("window").height;

export default function ProductDetails({ route, navigation }) {
  const { item } = route.params;
  const state = useNavigationState((state) => state);
  const routeName = state.routeNames[2];

  //const [cartPressed, setCartPressed] = useState(false)
  const [addCartTextPressed, setAddCartTextPressed] = useState(false);
  const [cart, setCart] = useState([]);

  const save = () => {
    if (addCartTextPressed === true) {
      setCart([...cart, item]);
    }
  };

  const saveToCart = async () => {
    try {
      if (addCartTextPressed === true) {
        await AsyncStorage.setItem("MyCart", JSON.stringify(cart));
      }
    } catch (err) {
      alert(err);
    }
  };

  const loadCart = async () => {
    try {
      let cart = await AsyncStorage.getItem("MyCart");
      cart = JSON.parse(cart);
      if (cart !== null) {
        setCart(cart);
      }
    } catch (err) {
      alert(err);
    }
  };

  useEffect(() => {
    loadCart();
  }, []);

  const colors = ["white", "black", "blue", "purple"];
  const listColors = colors.map((color, index) => (
    <View
      key={index}
      style={{
        backgroundColor: color,
        width: 40,
        height: 40,
        borderRadius: 40,
        marginHorizontal: 20,
      }}
    ></View>
  ));

  const headphone = ["S", "M", "L", "XL"];
  const listheadphone = headphone.map((headphone, index) => (
    <View
      key={index}
      style={{
        width: 50,
        height: 50,
        borderWidth: 1,
        borderColor: "#4580ff",
        justifyContent: "center",
        alignItems: "center",
        marginHorizontal: 20,
        borderRadius: 50,
      }}
    >
      <Text style={{ color: "#4580ff" }}>{shirtSize}</Text>
    </View>
  ));

  const renderProperItemData = () => {
    if (item.category === "headphone") {
      return (
        <View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Color
            </Text>
            <View style={styles.colorSizeItems}>{listColors}</View>
          </View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Size
            </Text>
            <View style={styles.colorSizeItems}>{listShirtSizes}</View>
          </View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
                marginTop: 10,
              }}
            >
              Review
            </Text>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#8f8f8f",
                marginVertical: 10,
              }}
            >
              No reviews
            </Text>
          </View>
          <View
            style={[
              styles.titles,
              { flexDirection: "row", alignItems: "center" },
            ]}
          >
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Promo Code
            </Text>

            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#4580ff",
                marginVertical: 10,
              }}
            >
              10% PO42GFD3
            </Text>
          </View>
          <View
            style={{
              marginHorizontal: 20,
              flexDirection: "row",
              alignItems: "center",
            }}
          >
            <AntDesign name="exclamationcircleo" size={12} color="black" />
            <Text
              style={{
                fontFamily: "MontserratRegular",
                fontSize: 12,
                marginLeft: 10,
              }}
            >
              Don't forget to use code at checkout
            </Text>
          </View>
        </View>
      );
    } else if (item.category === "shoe") {
      return (
        <View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Color
            </Text>

            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#8f8f8f",
                marginVertical: 10,
              }}
            >
              N/A
            </Text>
          </View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Size
            </Text>
            <View style={styles.colorSizeItems}>{listShoeSizes}</View>
          </View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
                marginTop: 10,
              }}
            >
              Review
            </Text>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#8f8f8f",
                marginVertical: 10,
              }}
            >
              No reviews
            </Text>
          </View>
          <View
            style={[
              styles.titles,
              { flexDirection: "row", alignItems: "center" },
            ]}
          >
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Promo Code
            </Text>

            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#4580ff",
                marginVertical: 10,
              }}
            >
              10% PO42GFD3
            </Text>
          </View>
          <View
            style={{
              marginHorizontal: 20,
              flexDirection: "row",
              alignItems: "center",
            }}
          >
            <AntDesign name="exclamationcircleo" size={12} color="black" />
            <Text
              style={{
                fontFamily: "MontserratRegular",
                fontSize: 12,
                marginLeft: 10,
              }}
            >
              Don't forget to use code at checkout
            </Text>
          </View>
        </View>
      );
    } else {
      return (
        <View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Color
            </Text>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#8f8f8f",
                marginVertical: 10,
              }}
            >
              N/A
            </Text>
          </View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Size
            </Text>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#8f8f8f",
                marginVertical: 10,
              }}
            >
              N/A
            </Text>
          </View>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Review
            </Text>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#8f8f8f",
                marginVertical: 10,
              }}
            >
              No reviews
            </Text>
          </View>
          <View
            style={[
              styles.titles,
              { flexDirection: "row", alignItems: "center" },
            ]}
          >
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#1c1c1c",
              }}
            >
              Promo Code
            </Text>

            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 16,
                color: "#4580ff",
                marginVertical: 10,
              }}
            >
              5% EER24Y75
            </Text>
          </View>
          <View
            style={{
              marginHorizontal: 20,
              flexDirection: "row",
              alignItems: "center",
            }}
          >
            <AntDesign name="exclamationcircleo" size={12} color="black" />
            <Text
              style={{
                fontFamily: "MontserratRegular",
                fontSize: 12,
                marginLeft: 10,
              }}
            >
              Don't forget to use code at checkout
            </Text>
          </View>
        </View>
      );
    }
  };

  const renderCorrectAddItemFunction = () => {
    setAddCartTextPressed(true);
    if (addCartTextPressed === true) {
      navigation.navigate("Cart", { item, cart, lastScreen: routeName });
    }
  };

  const renderCorrectText = () => {
    if (addCartTextPressed === true) {
      return <Text style={styles.addCartTextPressed}>go to cart</Text>;
    } else {
      return <Text style={styles.addCartText}>add to cart</Text>;
    }
  };

  return (
    <View style={styles.main}>
      <TouchableOpacity
        style={{ marginTop: 40, left: 20 }}
        onPress={() => navigation.goBack({ cart })}
      >
        <Ionicons name="arrow-back" size={30} style={{ color: "#f2f2f2" }} />
      </TouchableOpacity>

      <View style={styles.sixtyContainer}>
        <View style={styles.rotatedBox}></View>
        <Image source={item.image} style={styles.imageSize} />
      </View>

      <Text style={styles.itemName}>{item.name}</Text>
      <View style={styles.fortyContainer}>
        <TouchableOpacity
          style={styles.cartIconWrapper}
          onPress={() => [
            navigation.navigate("Cart", { item, cart, lastScreen: routeName }),
          ]}
        >
          <Ionicons name="cart-sharp" size={32} style={{ color: "#f2f2f2" }} />
        </TouchableOpacity>

        <ScrollView showsVerticalScrollIndicator={false}>
          <View style={styles.titles}>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 18,
                color: "#1c1c1c",
              }}
            >
              Description
            </Text>
            <Text
              style={{
                fontFamily: "MontserratSemiBold",
                fontSize: 13,
                color: "#707070",
                marginTop: 10,
              }}
            >
              {item.description}
            </Text>
          </View>

          <View>{renderProperItemData()}</View>
        </ScrollView>

        <View style={styles.priceAddCartWrapper}>
          <View style={styles.priceWrapper}>
            <Text style={styles.priceText}>$ {item.price}</Text>
          </View>
          <TouchableOpacity
            style={[
              addCartTextPressed === false
                ? styles.addCartWrapper
                : styles.addCartWrapperPressed,
            ]}
            onPress={() => [
              renderCorrectAddItemFunction(),
              save(),
              saveToCart(),
            ]}
          >
            {renderCorrectText()}
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

