import React from "react";
import {
  Text,
  View,
  Image,
  TouchableOpacity,
  TextInput,
  SafeAreaView,
  StyleSheet,
} from "react-native";
import { AntDesign } from "@expo/vector-icons";

export default function Login({ navigation }) {
  return (
    <View
      style={{
        flex: 1,
        alignItems: "center",
        backgroundColor: "snow",
        justifyContent: "center",
      }}
    >
      <Image
        style={{
          width: 250,
          height: 300,
          borderRadius: 10,
          marginBottom: 20,
        }}
        source={{
          uri: "https://images.unsplash.com/photo-1593359863503-f598684c806f?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTR8fGhlYWRwaG9uZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=700&q=60",
        }}
      />

      <TouchableOpacity
        style={{
          background: "#b0c4de",
          borderRadius: 10,
          flexDirection: "row",
          alignItems: "center",
          padding: 10,
          paddingHorizontal: 50,
          marginTop: 20,
        }}
      >
        <AntDesign name="user" size={24} color="black" />
        <Text
          style={{
            color: "black",
            fontSize: 15,
            fontWeight: "300",
            marginLeft: 15,
          }}
        >
          Username or email
        </Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={{
          alignItems: "center",
          background: "#20b2aa",
          borderRadius: 10,
          flexDirection: "row",
          padding: 10,
          paddingHorizontal: 60,
          marginTop: 20,
        }}
      >
        <AntDesign name="lock" size={24} color="black" />
        <Text
          style={{
            color: "black",
            marginLeft: 10,
            fontSize: 15,
            fontWeight: "300",
          }}
        >
          Password
        </Text>
      </TouchableOpacity>
      <Text style={{ marginTop: 10, fontWeight: "500", color: "black" }}>
        <TouchableOpacity
          onPress={() => {
            navigation.navigate("Home");
          }}
        >
          <Text style={{ color: "crimson", fontWeight: "bold" }}>Login</Text>
        </TouchableOpacity>
      </Text>
      <SafeAreaView>
        <TextInput
          style={styles.input}
          onChangeText={onChangeText}
          value={text}
        />
        <TextInput
          style={styles.input}
          placeholder="useless placeholder"
          keyboardType="numeric"
        />
      </SafeAreaView>
    </View>
  );
}
const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
