import { Ionicons } from "@expo/vector-icons";
import React from "react";
import {
  View,
  Text,
  TextInput,
  ScrollView,
  SafeAreaView,
  Image,
  FlatList,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
// import Product from "./Product";

const faceData = [
  {
    id: "1",
    title: "Kitty",
    price: "$45.00",
    image: require("../images/hess.jpg"),
  },
  {
    id: "2",
    title: "Neko",
    price: "$45.00",
    image: require("../images/neko.jpg"),
  },
  {
    id: "3",
    title: "sebas",
    price: "$45.00",
    image: require("../images/sebas.jpg"),
  },
  {
    id: "4",
    title: "npw",
    price: "$45.00",
    image: require("../images/npw.jpg"),
  },
  {
    id: "5",
    title: "portable",
    price: "$45.00",
    image: require("../images/portable.jpg"),
  },
];

export default function Home({ navigation }) {
  const renderItem = ({ item }) => {
    return (
      <View
        style={{
          height: 350,
          width: 200,
          backgroundColor: "#fff",
          left: 30,
          borderRadius: 15,
          marginRight: 20,
        }}
      >
        <TouchableOpacity>
          <View style={{ alignItems: "center", marginVertical: 10 }}>
            <Image source={item.image} style={{ width: 200, height: 200 }} />
          </View>
        </TouchableOpacity>
        <View style={{ marginHorizontal: 15, marginVertical: 10 }}>
          <Text styles={{ fontSize: 18, fontWeight: "bold" }}>
            {item.title}
          </Text>
          <Text
            styles={{ fontSize: 18, fontWeight: "bold", marginVertical: 10 }}
          >
            {item.price}
          </Text>
          <TouchableOpacity>
            <View
              style={{
                height: 50,
                width: 50,
                borderRadius: 25,
                backgroundColor: "#000",
                position: "absolute",
                right: 0,
                bottom: 0,
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <Ionicons name="md-cart-outline" size={20} color="red" />
            </View>
          </TouchableOpacity>
        </View>
      </View>
    );
  };

  return (
    <SafeAreaView>
      <View>
        <View
          style={{
            backgroundColor: "snow",
            flex: 1,
            paddingTop: 55,
            paddingHorizontal: 20,
          }}
        >
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <Ionicons name="md-settings-outline" size={24} color="black" />
            <View
              style={{
                alignItems: "center",

                marginLeft: 15,
                borderRadius: 10,
                flexDirection: "row",
                padding: 5,
                borderWidth: 2,
                marginTop: 5,
              }}
            >
              <Ionicons name="search" size={24} color="black" />
              <TextInput
                style={{
                  color: "black",
                  marginLeft: 10,
                  height: 10,
                  margin: 10,
                  borderColor: "snow",

                  padding: 10,
                  fontSize: 15,
                  fontWeight: "300, bold",
                }}
                placeholder="Search"
              ></TextInput>
            </View>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Cart");
              }}
            >
              <Ionicons name="cart-outline" size={24} color="black" />
            </TouchableOpacity>
          </View>

          <ScrollView scrollEventThrottle={16}>
            <View style={{ flex: 1, backgroundColor: "snow", paddingTop: 20 }}>
              <Text
                style={{
                  fontSize: 24,
                  fontWeight: "700",
                  paddingHorizontal: 20,
                }}
              >
                Popular Headphones
              </Text>

              <View style={{ height: 130, marginTop: 20 }}>
                <ScrollView
                  horizontal={true}
                  showsHorizontalScrollIndicator={false}
                ></ScrollView>
              </View>
            </View>
          </ScrollView>
        </View>
        <View style={{ marginVertical: 30 }}>
          <FlatList
            horizontal
            showsHorizontalScrollIndicator={false}
            data={faceData}
            renderItem={renderItem}
          />
        </View>
        <View style={{ marginVertical: 20, marginHorizontal: 30 }}>
          <Text style={{ fontSize: 22, fontWeight: "bold" }}>Trending</Text>
        </View>
      </View>
    </SafeAreaView>
  );
}
