import { Ionicons } from "@expo/vector-icons";
import React from "react";
import {
  View,
  Text,
  TextInput,
  ScrollView,
  SafeAreaView,
  Image,
  FlatList,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import Product from "./Product";

const faceData = [
  {
    id: "1",
    title: "Kitty",
    price: "$40.00",
    image: require("../images/kitty.jpg"),
  },
  {
    id: "2",
    title: "Kitty",
    price: "$40.00",
    image: require("../images/beats.jpeg"),
  },
];
export default function Home({ navigation }) {
  const renderItem = () => {
    <View
      style={{ height: 200, width: 200, backgroundColor: "red", left: 30 }}
    ></View>;
  };

  return (
    <SafeAreaView>
      <View>
        <View
          style={{
            backgroundColor: "snow",
            flex: 1,
            paddingTop: 55,
            paddingHorizontal: 20,
          }}
        >
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <Ionicons name="md-settings-outline" size={24} color="black" />
            <View
              style={{
                alignItems: "center",

                marginLeft: 15,
                borderRadius: 10,
                flexDirection: "row",
                padding: 5,
                borderWidth: 2,
                marginTop: 5,
              }}
            >
              <Ionicons name="search" size={24} color="black" />
              <TextInput
                style={{
                  color: "black",
                  marginLeft: 10,
                  height: 10,
                  margin: 10,
                  borderColor: "snow",

                  padding: 10,
                  fontSize: 15,
                  fontWeight: "300, bold",
                }}
                placeholder="Search"
              ></TextInput>
            </View>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Cart");
              }}
            >
              <Ionicons name="cart-outline" size={24} color="black" />
            </TouchableOpacity>
          </View>

          <ScrollView scrollEventThrottle={16}>
            <View style={{ flex: 1, backgroundColor: "snow", paddingTop: 20 }}>
              <Text
                style={{
                  fontSize: 24,
                  fontWeight: "700",
                  paddingHorizontal: 20,
                }}
              >
                Popular Headphones
              </Text>

              <View style={{ height: 130, marginTop: 20 }}>
                <ScrollView
                  horizontal={true}
                  showsHorizontalScrollIndicator={false}
                >
                  <Product />
                </ScrollView>
              </View>
              <View style={{ marginTop: 40, paddingHorizontal: 20 }}>
                <Text style={{ fontSize: 24, fontWeight: "700" }}>
                  Discover The Best
                </Text>
                <Text style={{ fontWeight: "100", marginTop: 10 }}>
                  Best Sellings
                </Text>
                <View style={{ width: 250, height: 200, marginTop: 20 }}>
                  <Image
                    style={{
                      flex: 1,
                      height: null,
                      width: null,
                      resizeMode: "cover",
                      borderRadius: 5,
                      borderWidth: 1,
                      borderColor: "snow",
                    }}
                    source={require("../images/neko.jpg")}
                  />
                </View>
              </View>
            </View>
          </ScrollView>
        </View>
      </View>
    </SafeAreaView>
  );
}
