import { Ionicons } from "@expo/vector-icons";
import React from "react";
import {
  View,
  Text,
  TextInput,
  ScrollView,
  SafeAreaView,
  Image,
  Dimensions,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import Product from "./Product";

export default function Home({ navigation }) {
  return (
    <SafeAreaView>
      <View>
        <View
          style={{
            backgroundColor: "snow",
            flex: 1,
            paddingTop: 55,
            paddingHorizontal: 20,
          }}
        >
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <Ionicons name="md-settings-outline" size={24} color="black" />
            <View
              style={{
                alignItems: "center",

                marginLeft: 15,
                borderRadius: 10,
                flexDirection: "row",
                padding: 5,
                borderWidth: 2,
                marginTop: 5,
              }}
            >
              <Ionicons name="search" size={24} color="black" />
              <TextInput
                style={{
                  color: "black",
                  marginLeft: 10,
                  height: 10,
                  margin: 10,
                  borderColor: "snow",

                  padding: 10,
                  fontSize: 15,
                  fontWeight: "300, bold",
                }}
                placeholder="Search"
              ></TextInput>
            </View>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Cart");
              }}
            >
              <Ionicons name="cart-outline" size={24} color="black" />
            </TouchableOpacity>
          </View>

          <ScrollView scrollEventThrottle={16}>
            <View style={{ flex: 1, backgroundColor: "snow", paddingTop: 20 }}>
              <Text
                style={{
                  fontSize: 24,
                  fontWeight: "700",
                  paddingHorizontal: 20,
                }}
              >
                Popular Headphones
              </Text>

              <View style={{ height: 130, marginTop: 20 }}>
                <ScrollView
                  horizontal={true}
                  showsHorizontalScrollIndicator={false}
                >
                  <Product />
                </ScrollView>
              </View>
              <View style={{ marginTop: 40, paddingHorizontal: 20 }}>
                <Text style={{ fontSize: 24, fontWeight: "700" }}>
                  Discover The Best
                </Text>
                <Text style={{ fontWeight: "100", marginTop: 10 }}>
                  Best Sellings
                </Text>
                <View style={{ width: 250, height: 200, marginTop: 20 }}>
                  <Image
                    style={{ flex: 1, height: null, width: null }}
                    source={{
                      uri: "https://media.istockphoto.com/photos/stay-alone-at-home-during-covid19-and-relax-at-spare-time-with-music-picture-id1302986496?b=1&k=20&m=1302986496&s=170667a&w=0&h=iOh2tJX5RL5cbqYFdrRTlFOq-E6vpR-MPDMBXj9ZOEU=",
                    }}
                  />
                </View>
              </View>
            </View>
          </ScrollView>
        </View>
      </View>
    </SafeAreaView>
  );
}
