import { Ionicons } from "@expo/vector-icons";
import React from "react";
import {
  View,
  Text,
  TextInput,
  ScrollView,
  SafeAreaView,
  Image,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";

export default function Home({ navigation }) {
  return (
    <SafeAreaView>
      <View>
        <View
          style={{
            backgroundColor: "snow",
            flex: 1,
            paddingTop: 55,
            paddingHorizontal: 20,
          }}
        >
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <Ionicons name="md-settings-outline" size={24} color="black" />
            <View
              style={{
                alignItems: "center",

                marginLeft: 15,
                borderRadius: 10,
                flexDirection: "row",
                padding: 5,
                borderWidth: 2,
                marginTop: 5,
              }}
            >
              <Ionicons name="search" size={24} color="black" />
              <TextInput
                style={{
                  color: "black",
                  marginLeft: 10,
                  height: 10,
                  margin: 10,
                  borderColor: "snow",

                  padding: 10,
                  fontSize: 15,
                  fontWeight: "300, bold",
                }}
                placeholder="Search"
              ></TextInput>
            </View>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Cart");
              }}
            >
              <Ionicons name="cart-outline" size={24} color="black" />
            </TouchableOpacity>
          </View>

          <ScrollView scrollEventThrottle={16}>
            <View style={{ flex: 1, backgroundColor: "snow", paddingTop: 20 }}>
              <Text
                style={{
                  fontSize: 24,
                  fontWeight: "700",
                  paddingHorizontal: 20,
                }}
              >
                Welcome To The Exciting World Of Music
              </Text>

              <View style={{ height: 130, marginTop: 20 }}>
                <ScrollView>
                  <View
                    style={{
                      height: 130,
                      width: 130,
                      marginLeft: 20,
                      borderWidth: 0.5,
                      borderColor: "#ddddd",
                    }}
                  >
                    <View style={{ flex: 2 }}>
                      <Image
                        style={{
                          flex: 1,
                          height: null,
                          weight: null,
                          resizeMode: "cover",
                        }}
                        source={require("../images/kitty.jpg")}
                      />
                    </View>
                    <View style={{ flex: 1, paddingLeft: 10, paddingTop: 10 }}>
                      <Text>Hello kitty</Text>
                    </View>
                  </View>
                </ScrollView>
              </View>
            </View>
          </ScrollView>
        </View>
      </View>
    </SafeAreaView>
  );
}
