import React from "react";
import { View, Text, Image } from "react-native";

export default function Product() {
  return (
    <View
      style={{
        height: 130,
        width: 130,
        marginLeft: 20,
        borderWidth: 0.5,
        borderColor: "snow",
      }}
    >
      <View style={{ flex: 2 }}>
        <Image
          style={{
            flex: 1,
            height: null,
            weight: null,
            resizeMode: "cover",
          }}
          source={require("../images/kitty.jpg")}
        />
      </View>
      <View style={{ flex: 1, paddingLeft: 10, paddingTop: 10 }}>
        <Text>BBY</Text>
      </View>
    </View>
  );
}
